
(function() {
    window.breakpoints = function(points) {
        window.addEventListener('resize', () => {
            let width = window.innerWidth;
            for (let point in points) {
                let min = parseInt(points[point][0]) || 0;
                let max = parseInt(points[point][1]) || Infinity;
                if (width >= min && width <= max) {
                    document.body.setAttribute('data-breakpoint', point);
                }
            }
        });
    };
})();
