
(function($) {
    $.fn.scrolly = function() {
        this.on('click', function(event) {
            const target = document.querySelector(this.getAttribute('href'));
            if (target) {
                event.preventDefault();
                window.scrollTo({
                    top: target.offsetTop,
                    behavior: 'smooth'
                });
            }
        });
        return this;
    };
})(jQuery);
